package stu_20250601;

public class leetcode_424 {

    public static void main(String[] args) {
        System.out.println(sol_424("", 100));
    }

    private static int sol_424(String s, int k) {

        int[] ary = new int[26];
        int len = 0;
        int maxalphaCount = 0;
        int diffalpha = 0;

        for (int i = 0; i < s.length(); i++) {
            ary[s.charAt(i) - 'A']++;

            maxalphaCount = Math.max(maxalphaCount, ary[s.charAt(i) - 'A']);

            while ((i + 1 - diffalpha) - maxalphaCount > k) {
                ary[s.charAt(diffalpha) - 'A']--;
                diffalpha++;
            }

            len = Math.max(len, i + 1 - diffalpha);
        }


        return len;
    }
}
